import React, { useEffect, useState } from "react";
import { View, Text, Image, TouchableOpacity, ScrollView } from "react-native";
import BouncyCheckbox from "react-native-bouncy-checkbox";
import ViewCart from "./ViewCart";
import { useDispatch } from "react-redux";
const foods = [
  {
    title: "Lasagna",
    description: "With butter lettuce, tomato and sauce becahmel",
    price: "13.50$",
    image: "https://picsum.photos/200/300",
  },
  {
    title: "Tandoori Chicken",
    description: "Amazing Indian Dish",
    price: "23.50$",
    image: "https://picsum.photos/200/300",
  },
  {
    title: "Falafel",
    description: "Pita with Falafel",
    price: "5.00$",
    image: "https://picsum.photos/200/300",
  },
  {
    title: "Italian Pizza",
    description: "Original Italian pizza with toppings",
    price: "9.75$",
    image: "https://picsum.photos/200/300",
  },
  {
    title: "Lasagna",
    description: "With butter lettuce, tomato and sauce becahmel",
    price: "13.50$",
    image: "https://picsum.photos/200/300",
  },
  {
    title: "Tandoori Chicken",
    description: "Amazing Indian Dish",
    price: "23.50$",
    image: "https://picsum.photos/200/300",
  },
  {
    title: "Falafel",
    description: "Pita with Falafel",
    price: "5.00$",
    image: "https://picsum.photos/200/300",
  },
  {
    title: "Italian Pizza",
    description: "Original Italian pizza with toppings",
    price: "9.75$",
    image: "https://picsum.photos/200/300",
  },
];

function MenuItem(props) {
  const dispatch = useDispatch();
  let selectItem = (item) => {
    dispatch({
      type: "ADD_TO_CART",
      payload: item,
    });
  };
  return (
    <ScrollView style={{ height: "73%" }} showsVerticalScrollIndicator={false}>
      {foods.map((item, index) => {
        return (
          <View key={index} style={{ marginTop: 5 }}>
            <BouncyCheckbox />

            <View style={{ flexDirection: "row" }}>
              {/* <BouncyCheckbox
                key={index}
                fillColor="green"
                style={{
                  marginLeft: 10,
                }}
              /> */}
              <FoodInfo
                title={item.title}
                description={item.description}
                price={item.price}
              ></FoodInfo>
              <FoodImage image={item.image}></FoodImage>
            </View>
            <View
              style={{
                borderBottomColor: "lightgrey",
                borderBottomWidth: 1,
                padding: 2,
                marginBottom: 10,
                marginHorizontal: 15,
              }}
            />
          </View>
        );
      })}
    </ScrollView>
  );
}

const FoodInfo = (props) => {
  return (
    <View
      style={{
        margin: 10,
        justifyContent: "space-evenly",
        width: 280,
      }}
    >
      <Text style={{ fontWeight: "600", fontSize: 24 }}>{props.title}</Text>
      <Text>{props.description}</Text>
      <Text>{props.price}</Text>
    </View>
  );
};
const FoodImage = (props) => {
  return (
    <View style={{ marginBottom: 10 }}>
      <Image
        source={{ uri: props.image }}
        style={{
          height: 100,
          width: 100,
          borderRadius: 10,
          marginHorizontal: -30,
        }}
      ></Image>
    </View>
  );
};
export default MenuItem;
