import React from "react";
import {
  View,
  Text,
  Image,
  TouchableOpacity,
  ImageBackground,
} from "react-native";
import MaterialCommunityIcons from "react-native-vector-icons/MaterialCommunityIcons";

function RestaurantItem(props) {
  return (
    <TouchableOpacity activeOpacity={0.1}>
      <View
        style={{
          marginTop: 10,
          padding: 15,
          backgroundColor: "white",
        }}
      >
        {/*Image */}
        <restaurantImage />
        {/*Info */}
        <restaurantInfo />
      </View>
    </TouchableOpacity>
  );
}

const restaurantImage = () => (
  <>
    <View>
      <ImageBackground
        source={{
          uri: "https://picsum.photos/200/",
        }}
        style={{
          width: "100%",
          height: 180,
        }}
      >
        <TouchableOpacity>
          <MaterialCommunityIcons
            name="heart-outline"
            size={24}
            color={"#fff"}
            style={{
              position: "absolute",
              right: 20,
              top: 20,
            }}
          ></MaterialCommunityIcons>
        </TouchableOpacity>
      </ImageBackground>
    </View>
  </>
);

const restaurantInfo = () => (
  <View
    style={{
      flexDirection: "row",
      justifyContent: "space-between",
      alignItems: "center",
      marginTop: 10,
    }}
  >
    <View>
      <Text
        style={{
          fontWeight: "bold",
          fontSize: 16,
        }}
      >
        Formhouse Kitchen Thai Cousine
      </Text>
      <Text
        style={{
          fontSize: 16,
          color: "grey",
        }}
      >
        30-45 min
      </Text>
    </View>
    <View
      style={{
        backgroundColor: "#eee",
        height: 30,
        width: 30,
        alignItems: "center",
        borderRadius: 20,
        justifyContent: "center",
      }}
    >
      <Text
        style={{
          fontWeight: "bold",
          fontSize: 16,
        }}
      >
        4.5
      </Text>
    </View>
  </View>
);

export default RestaurantItem;
