import React, { useEffect, useState } from "react";
import { View, Text, Image, TouchableOpacity, ScrollView } from "react-native";
import BouncyCheckbox from "react-native-bouncy-checkbox";
import ViewCart from "./ViewCart";
const foods = [
  {
    title: "Lasagna",
    description: "With butter lettuce, tomato and sauce becahmel",
    price: "13.50$",
    image: "https://picsum.photos/200/300",
  },
  {
    title: "Tandoori Chicken",
    description: "Amazing Indian Dish",
    price: "23.50$",
    image: "https://picsum.photos/200/300",
  },
  {
    title: "Falafel",
    description: "Pita with Falafel",
    price: "5.00$",
    image: "https://picsum.photos/200/300",
  },
  {
    title: "Italian Pizza",
    description: "Original Italian pizza with toppings",
    price: "9.75$",
    image: "https://picsum.photos/200/300",
  },
  {
    title: "Lasagna",
    description: "With butter lettuce, tomato and sauce becahmel",
    price: "13.50$",
    image: "https://picsum.photos/200/300",
  },
  {
    title: "Tandoori Chicken",
    description: "Amazing Indian Dish",
    price: "23.50$",
    image: "https://picsum.photos/200/300",
  },
  {
    title: "Falafel",
    description: "Pita with Falafel",
    price: "5.00$",
    image: "https://picsum.photos/200/300",
  },
  {
    title: "Italian Pizza",
    description: "Original Italian pizza with toppings",
    price: "9.75$",
    image: "https://picsum.photos/200/300",
  },
];

function MenuItem(props) {
  return (
    <>
      <ScrollView showsVerticalScrollIndicator={false}>
        {foods.map((food, index) => (
          <View key={index}>
            <View style={styles.menuItemStyle}>
              {hideCheckbox ? (
                <></>
              ) : (
                <BouncyCheckbox
                  iconStyle={{ borderColor: "lightgray", borderRadius: 0 }}
                  fillColor="green"
                  isChecked={isFoodInCart(food, cartItems)}
                  onPress={(checkboxValue) => selectItem(food, checkboxValue)}
                />
              )}
              <FoodInfo food={food} />
              <FoodImage food={food} marginLeft={marginLeft ? marginLeft : 0} />
            </View>
            <Divider
              width={0.5}
              orientation="vertical"
              style={{ marginHorizontal: 20 }}
            />
          </View>
        ))}
      </ScrollView>
    </>
  );
}

const FoodInfo = (props) => {
  return (
    <View
      style={{
        margin: 10,
        justifyContent: "space-evenly",
        width: 280,
      }}
    >
      <Text style={{ fontWeight: "600", fontSize: 24 }}>{props.title}</Text>
      <Text>{props.description}</Text>
      <Text>{props.price}</Text>
    </View>
  );
};
const FoodImage = (props) => {
  return (
    <View style={{ marginBottom: 10 }}>
      <Image
        source={{ uri: props.image }}
        style={{
          height: 100,
          width: 100,
          borderRadius: 10,
          marginHorizontal: -30,
        }}
      ></Image>
    </View>
  );
};

const styles = StyleSheet.create({
  menuItemStyle: {
    flexDirection: "row",
    justifyContent: "space-between",
    margin: 20,
  },

  titleStyle: {
    fontSize: 19,
    fontWeight: "600",
  },
});
export default MenuItem;
