import React from "react";
import {
  View,
  Text,
  Image,
  TouchableOpacity,
  ImageBackground,
} from "react-native";
import MaterialCommunityIcons from "react-native-vector-icons/MaterialCommunityIcons";

export const localRestaurants = [
  {
    name: "Beachside Bar",
    image_url: "https://picsum.photos/200/",
    categories: ["Cafe", "Bar"],
    price: "$$$",
    reviews: 1244,
    rating: 4.5,
  },
  {
    name: "Benihana",
    image_url: "https://picsum.photos/200/",
    categories: ["Cafe", "Bar"],
    price: "$$$",
    reviews: 1244,
    rating: 3.9,
  },
  {
    name: "Pizza Place",
    image_url: "https://picsum.photos/200/",
    categories: ["Cafe", "Bar"],
    price: "$",
    reviews: 1244,
    rating: 3.5,
  },
];

function RestaurantItems(props) {
  return (
    <TouchableOpacity activeOpacity={0.9} style={{ marginBottom: 30 }}>
      {props.restaurantData.map((restuarant, index) => (
        <View
          key={index}
          style={{
            marginTop: 10,
            padding: 15,
            backgroundColor: "white",
          }}
        >
          {/*Image */}
          <RestuarantImage image={restuarant.image_url} />
          {/*Info */}
          <RestuarantInfo rating={restuarant.rating} name={restuarant.name} />
        </View>
      ))}
    </TouchableOpacity>
  );
}

const RestuarantImage = (props) => (
  <>
    <View>
      <ImageBackground
        source={{
          uri: `${props.image}`,
        }}
        style={{
          width: "100%",
          height: 180,
        }}
      >
        <TouchableOpacity>
          <MaterialCommunityIcons
            name="heart-outline"
            size={24}
            color={"#fff"}
            style={{
              position: "absolute",
              right: 20,
              top: 20,
            }}
          ></MaterialCommunityIcons>
        </TouchableOpacity>
      </ImageBackground>
    </View>
  </>
);

const RestuarantInfo = (props) => (
  <View
    style={{
      flexDirection: "row",
      justifyContent: "space-between",
      alignItems: "center",
      marginTop: 10,
    }}
  >
    <View>
      <Text
        style={{
          fontWeight: "bold",
          fontSize: 16,
        }}
      >
        {props.name}
      </Text>
      <Text
        style={{
          fontSize: 16,
          color: "grey",
        }}
      >
        30-45 min
      </Text>
    </View>
    <View
      style={{
        backgroundColor: "#eee",
        height: 30,
        width: 30,
        alignItems: "center",
        borderRadius: 20,
        justifyContent: "center",
      }}
    >
      <Text
        style={{
          fontWeight: "bold",
          fontSize: 16,
        }}
      >
        {props.rating}
      </Text>
    </View>
  </View>
);

export default RestaurantItems;
