import React, { useEffect, useState } from "react";
import { View, Text, SafeAreaView, ScrollView } from "react-native";
import HeaderTabs from "../components/HeaderTabs";
import SearchBar from "../components/SearchBar";
import Categories from "../components/categories";
import RestaurantItems, {
  localRestaurants,
} from "../components/RestaurantItem";

const YELP_API_KEY =
  "czEbzeadHLcoIFm5EFt7G9JJMYzNldInKc_WbpEcQs6E1hwLQkHVYBIZhJuaCKLSmfUXkSg-NKaMIWhqz3UaLTzPrLtMntutfKDTBsgVlC3ZgXxCkO1qtH2r9cDRYXYx";

export function Home(params) {
  const [restaurantData, setRestaurantData] = React.useState(localRestaurants);
  const [city, setCity] = useState("SanDiego");
  const [activeTab, setActiveTab] = useState("Delivery");

  const getRestaurantsFromYelp = () => {
    const yelpUrl = `https://api.yelp.com/v3/businesses/search?term=restaurants&location=${city}`;

    const apiOptions = {
      headers: {
        Authorization: `Bearer ${YELP_API_KEY}`,
      },
    };
    return fetch(yelpUrl, apiOptions)
      .then((res) => res.json())
      .then((json) =>
        setRestaurantData(
          json.businesses.filter((business) =>
            business.transactions.includes(activeTab.toLowerCase())
          )
        )
      );
  };

  useEffect(() => {
    getRestaurantsFromYelp();
  }, [city, activeTab]);

  return (
    <SafeAreaView
      style={{
        backgroundColor: "#eee",
        flex: 1,
      }}
    >
      <View
        style={{
          backgroundColor: "white",
          padding: 15,
        }}
      >
        <HeaderTabs
          activeTab={activeTab}
          setActiveTab={setActiveTab}
        ></HeaderTabs>
        <SearchBar cityHandler={setCity}></SearchBar>
      </View>
      <ScrollView>
        <Categories />
        <RestaurantItems restaurantData={restaurantData} />
      </ScrollView>
    </SafeAreaView>
  );
}

export default Home;
